export default function _arity(n, fn) &#123;  /* eslint-disable no-unused-vars */  switch (n) &#123;    case 0: return function() &#123; return fn.apply(this, arguments); &#125;;    case 1: return function(a0) &#123; return fn.apply(this, arguments); &#125;;    case 2: return function(a0, a1) &#123; return fn.apply(this, arguments); &#125;;    // ...    case 10: return function(a0, a1, a2, a3, a4, a5, a6, a7, a8, a9) &#123; return fn.apply(this, arguments); &#125;;    default: throw new Error(&#x27;First argument to _arity must be a non-negative integer no greater than ten&#x27;);  &#125;&#125;

var curryN = _curry2(function curryN(length, fn) &#123;  if (length === 1) &#123;    return _curry1(fn);  &#125;  return _arity(length, _curryN(length, [], fn));&#125;);

在参数较少时使用其他的内置方法  
function curryN(length, fn) &#123;  return _curry2(function (length, fn) &#123;    return length === 1      ? _curry1(fn)      : length === 2        ? _curry2(fn)        : length === 3          ? _curry3(fn)          : _arity(length, _curryN(length, [], fn))  &#125;)&#125;


_curryN 核心方法下面是一段糟糕的代码
receive保持了源数组的引用，不符合函数式编成数据不可变的思想
每次添加新参数，都要查询可添加的位置
// 1. 真实参数个数和length项等 则执行函数// 2. 实时记录占位符的位置， 新参数优先放到占位符function _curryN(length, receive, fn) &#123;  return function fN() &#123;    var index = 0;    var zw = 0;    function findzw(zw, receive) &#123;      while (zw &lt; receive.length) &#123;        if (_isPlaceholder(receive[zw])) &#123;          //指向占位符的前一位， 方便后面处理          return zw - 1        &#125;        zw++      &#125;      return zw - 1    &#125;    // 找到占位符的位置    zw = findzw(zw, receive);    while (index &lt; arguments.length) &#123;      receive[zw + 1] = arguments[index];      //更新之后重新查找占位符, 条过当前的占位符      zw = findzw(zw + 1, receive);      index++;    &#125;    // 没有占位符 则位置+1为参数长度    if (zw + 1 === length) &#123;      return fn.apply(this, receive)    &#125; else &#123;      // 计算剩余参数      var j = 0;      var len = 0      while (j &lt; receive.length) &#123;        if (!_isPlaceholder(receive[index])) &#123;          len++;        &#125;        j++      &#125;      return _arity(length - len, _curryN(length, receive, fn))    &#125;  &#125;&#125;

源码的实现
function _curryN(length, receive, fn) &#123;  return function () &#123;    // 复制已接受参数的数组    var combined = [];    // 遍历入参的索引    var argsIdx = 0;    // 剩余参数    var left = length;    // 遍历已接受参数索引    var combinedIdx = 0;    // 如果参数没有复制完成，或新如参没有添加完成则继续执行    while (combinedIdx &lt; received.length || argsIdx &lt; arguments.length) &#123;      var result;      // 通过条件语句处理了占位符的问题      // 如果老参数没有复制完成，且复制的值不是占位符，或者入参已经都处理了，直接取出当前的老参数，用于复制到新的数组中      // 通过双指针解决占位符的问题      if (combinedIdx &lt; received.length &amp;&amp;        (!_isPlaceholder(received[combinedIdx]) ||          argsIdx &gt;= arguments.length)) &#123;        result = received[combinedIdx];      &#125; else &#123;        // 如果老参数都复制完，或者时占位符，或者还有新参数没有复制        // 取一个新参数加到数组中        result = arguments[argsIdx];        argsIdx += 1;      &#125;      // 添加到新数组中      combined[combinedIdx] = result;      // 如果不是占位符剩余参数 -1      if (!_isPlaceholder(result)) &#123;        left -= 1;      &#125;      combinedIdx += 1;    &#125;    // 如果没有剩余参数直接执行    return left &lt;= 0      ? fn.apply(this, combined)      // 否则通过_arity固定新函数的参数个数      : _arity(left, _curryN(length, combined, fn));  &#125;;&#125;

