getBoundPositionfunction getBoundPosition(  draggable: Draggable,  x: number,  y: number): [number, number] &#123;  // If no bounds, short-circuit and move on  if (!draggable.props.bounds) return [x, y];  // Clone new bounds  let &#123; bounds &#125; = draggable.props;  bounds = typeof bounds === &quot;string&quot; ? bounds : cloneBounds(bounds);  const node = findDOMNode(draggable);  if (typeof bounds === &quot;string&quot;) &#123;    const &#123; ownerDocument &#125; = node;    const ownerWindow = ownerDocument.defaultView;    let boundNode;    if (bounds === &quot;parent&quot;) &#123;      boundNode = node.parentNode;    &#125; else &#123;      boundNode = ownerDocument.querySelector(bounds);    &#125;    if (!(boundNode instanceof ownerWindow.HTMLElement)) &#123;      throw new Error(        &#x27;Bounds selector &quot;&#x27; + bounds + &#x27;&quot; could not find an element.&#x27;      );    &#125;    const boundNodeEl: HTMLElement = boundNode; // for Flow, can&#x27;t seem to refine correctly    const nodeStyle = ownerWindow.getComputedStyle(node);    const boundNodeStyle = ownerWindow.getComputedStyle(boundNodeEl);    // Compute bounds. This is a pain with padding and offsets but this gets it exactly right.    bounds = &#123;      left:        -node.offsetLeft +        int(boundNodeStyle.paddingLeft) +        int(nodeStyle.marginLeft),      top:        -node.offsetTop +        int(boundNodeStyle.paddingTop) +        int(nodeStyle.marginTop),      right:        innerWidth(boundNodeEl) -        outerWidth(node) -        node.offsetLeft +        int(boundNodeStyle.paddingRight) -        int(nodeStyle.marginRight),      bottom:        innerHeight(boundNodeEl) -        outerHeight(node) -        node.offsetTop +        int(boundNodeStyle.paddingBottom) -        int(nodeStyle.marginBottom),    &#125;;  &#125;  // Keep x and y below right and bottom limits...  if (isNum(bounds.right)) x = Math.min(x, bounds.right);  if (isNum(bounds.bottom)) y = Math.min(y, bounds.bottom);  // But above left and top limits.  if (isNum(bounds.left)) x = Math.max(x, bounds.left);  if (isNum(bounds.top)) y = Math.max(y, bounds.top);  return [x, y];&#125;

handleDragStartconst handleDragStart: EventHandler&lt;MouseTouchEvent&gt; = (e) =&gt; &#123;  // 用于自定义的 onMouseDown 事件  this.props.onMouseDown(e);  // 任意鼠标按键都一颗拖动,否则只有鼠标左键可以拖动  if (    !this.props.allowAnyClick &amp;&amp;    typeof e.button === &quot;number&quot; &amp;&amp;    e.button !== 0  )    return false;  // 查找到需要拖动的元素  const thisNode = this.findDOMNode();  if (!thisNode || !thisNode.ownerDocument || !thisNode.ownerDocument.body) &#123;    throw new Error(&quot;&lt;DraggableCore&gt; not mounted on DragStart!&quot;);  &#125;  const &#123; ownerDocument &#125; = thisNode;  // 控制可拖动区域和不可拖动区域  // handle cancel 属性传入的是控制区域的元素标签名称  // 用原生的 matches 方法检查这个标签是否在拖动元素的内部,如果有就停止执行  element.matches(&#x27;p&#x27;)  // 这是真正的 onStart 方法还没有执行,不需要重置状态  if (    this.props.disabled ||    !(e.target instanceof ownerDocument.defaultView.Node) ||    (this.props.handle &amp;&amp;      !matchesSelectorAndParentsTo(e.target, this.props.handle, thisNode)) ||    (this.props.cancel &amp;&amp;      matchesSelectorAndParentsTo(e.target, this.props.cancel, thisNode))  ) &#123;    return;  &#125;  // Prevent scrolling on mobile devices, like ipad/iphone.  // Important that this is after handle/cancel.  if (e.type === &quot;touchstart&quot;) e.preventDefault();  // Set touch identifier in component state if this is a touch event. This allows us to  // distinguish between individual touches on multitouch screens by identifying which  // touchpoint was set to this element.  // 记录触摸点的 id  const touchIdentifier = getTouchIdentifier(e);  this.setState(&#123; touchIdentifier &#125;);  // Get the current drag point from the event. This is used as the offset.  // 首先如果存在触摸点,则回去touch 事件,否则获取原生的 event  // 通过 findDOMNode 查找需要拖动的元素, 获取用于计算相对位置的元素 offsetParent 如果没有这个属性就用上级 parentNode || document  // const x = (evt.clientX + offsetParent.scrollLeft - offsetParentRect.left) / scale;  // const y = (evt.clientY + offsetParent.scrollTop - offsetParentRect.top) / scale;  // 获取相对于父元素的鼠标位置  const position = getCont