统一的错误处理http-errors 可以统一错误抛出，避免自定义错误对象。
var E = require(&quot;http-errors&quot;);throw new E.BadRequest([&quot;报错了&quot;]);

可以使用统一的错误处理函数
module.exports = function errorHandle(res, err) &#123;  let status = 500;  let message = [];  if (err instanceof E.HttpError) &#123;    status = err.status;    message = err.message;  &#125;  res.status(status).json(&#123;    status: false,    errors: message,  &#125;);&#125;;

腾讯 OSS 服务端上传// 引入模块var COS = require(&quot;cos-nodejs-sdk-v5&quot;);var crypto = require(&quot;crypto&quot;);var multer = require(&quot;multer&quot;);// 创建实例var cos = new COS(&#123;  SecretId: &quot;xxx&quot;,  SecretKey: &quot;xxx&quot;,&#125;);// 存储桶名称，由bucketname-appid 组成，appid必须填入，可以在COS控制台查看存储桶名称。 https://console.cloud.tencent.com/cos5/bucketvar Bucket = &quot;xxx-1255610650&quot;;// 存储桶Region可以在COS控制台指定存储桶的概览页查看 https://console.cloud.tencent.com/cos5/bucket/// 关于地域的详情见 https://cloud.tencent.com/document/product/436/6224var Region = &quot;ap-nanjing&quot;;const storage = multer.memoryStorage();const upload = multer(&#123;  storage: storage,  limits: &#123; fileSize: 10 * 1024 * 1024 &#125;, // 限制文件大小（10MB）  fileFilter: (req, file, cb) =&gt; &#123;    // 可选：限制文件类型    const allowedTypes = [&quot;image/jpeg&quot;, &quot;image/png&quot;];    if (allowedTypes.includes(file.mimetype)) &#123;      cb(null, true);    &#125; else &#123;      cb(new Error(&quot;只允许上传 JPG/PNG 文件&quot;));    &#125;  &#125;,&#125;);const push = async (req, res) =&gt; &#123;  if (!req.file) &#123;    throw new E[400]([&quot;你没有选择文件&quot;]);  &#125;  const ext = req.file.originalname.split(&quot;.&quot;).pop();  const fileName = `$&#123;crypto.randomBytes(64).toString(&quot;hex&quot;)&#125;.$&#123;ext&#125;`;  return cos.putObject(&#123;    Bucket,    Region,    Key: fileName, // 对象存储路径    Body: req.file.buffer, // 文件内容（Buffer）    ContentType: req.file.mimetype, // 文件类型  &#125;);&#125;;module.exports = &#123; middleware: upload.single(&quot;file&quot;), push &#125;;

接口添加中间件
const &#123; middleware: uploadMiddleware &#125; = require(&quot;./utils/upload&quot;);app.use(&quot;/upload&quot;, uploadMiddleware, uploadRouter);

router.post(&quot;/&quot;, async function (req, res, next) &#123;  try &#123;    if (!req.file) &#123;      throw new Error();    &#125;    const result = await push(req);    // 同时保存到数据库中    // ...    res.json(result);  &#125; catch (error) &#123;    res.errorHandle(error);  &#125;&#125;);

腾讯 OSS 客户端上传需要先获取临时令牌,服务端提供接口, allowPrefix allowActions 必填
服务端可以把桶的名称，地区统一返回
router.get(&quot;/sts&quot;, (req, res, next) =&gt; &#123;  var config = &#123;    secretId: &quot;xxx&quot;, // 固定密钥    secretKey: &quot;xxx&quot;, // 固定密钥    proxy: &quot;&quot;,    durationSeconds: 1800,    // host: &#x27;sts.tencentcloudapi.com&#x27;, // 域名，非必须，默认为 sts.tencentcloudapi.com    endpoint: &quot;sts.tencentcloudapi.com&quot;, // 域名，非必须，与host二选一，默认为 sts.tencentcloudapi.com    // 放行判断相关参数    bucket: &quot;node-learn-1255610650&quot;,    region: &quot;ap-nanjing&quot;,    allowPrefix: &quot;*&quot;,    allowActions: [      // 简单上传      &quot;name/cos:PutObject&quot;,      // 分块上传      &quot;name/cos:InitiateMultipartUpload&quot;,      &quot;name/cos:ListMultipartUploads&quot;,      &quot;name/cos:ListParts&quot;,      &quot;name/cos:UploadPart&quot;,      &quot;name/cos:CompleteMultipartUpload&quot;,    ],  &#125;;  var shortBucketName = config.bucket.substr(0, config.bucket.lastIndexOf(&quot;-&quot;));  var appId = config.bucket.substr(1 + config.bucket.lastIndexOf(&quot;-&quot;));  var policy = &#123;    version: &quot;2.0&quot;,    statement: [      &#123;        action: config.allowActions,        effect: &quot;allow&quot;,        principal: &#123; qcs: [&quot;*&quot;] &#125;,        resource: [          &quot;qcs::cos:&quot; +            config.region +            &quot;:uid/&quot; +            appId +            &quot;:prefix//&quot; +            appId +            &quot;/&quot; +            shortBucketName +            &quot;/&quot; +            config.allowPrefix,        ],        // condition生效条件，关于 condition 的详细设置规则和COS支持的condition类型可以参考https://cloud.tencent.com/document/product/436/71306        // &#x27;condition&#x27;: &#123;        //   // 比如限定ip访问        //   &#x27;ip_equal&#x27;: &#123;        //     &#x27;qcs:ip&#x27;: &#x27;10.121.2.10/24&#x27;        //   &#125;        // &#125;      &#125;,    ],  &#125;;  STS.getCredential(    &#123;      secretId: config.secretId,      secretKey: config.secretKey,      proxy: config.proxy,      durationSeconds: config.durationSeconds,      endpoint: config.endpoint,      policy: policy,    &#125;,    function (err, tempKeys) &#123;      res.json(&#123;        ...tempKeys,        bucket: &quot;xxx-1255610650&quot;,        region: &quot;ap-nanjing&quot;,      &#125;);    &#125;  );&#125;);

客户端调用
fetch(&quot;/upload/sts&quot;)  .then((res) =&gt; res.json())  .then((res) =&gt; &#123;    const cos = new COS(&#123;      SecretId: res.credentials.tmpSecretId, // sts服务下发的临时 secretId      SecretKey: res.credentials.tmpSecretKey, // sts服务下发的临时 secretKey      SecurityToken: res.credentials.sessionToken, // sts服务下发的临时 SessionToken      StartTime: res.startTime, // 建议传入服务端时间，可避免客户端时间不准导致的签名错误      ExpiredTime: res.expiredTime, // 临时密钥过期时间    &#125;);    cos.uploadFile(      &#123;        Bucket: res.bucket,        Region: res.region,        Key: &quot;xxx.txt&quot;,        Body: document.getElementById(&quot;file&quot;).files[0], // 要上传的文件对象。        onProgress: function (progressData) &#123;          console.log(&quot;上传进度：&quot;, progressData);        &#125;,      &#125;,      function (err, data) &#123;        console.log(&quot;上传结束&quot;, err || data);      &#125;    );  &#125;);

redis安装 redis 并提供一个简单的工具文件
// redis-utils.jsconst &#123; createClient &#125; = require(&quot;redis&quot;);// 全局 Redis 客户端实例let client = null;/** * 初始化 Redis 客户端（单例模式） */const initializeRedis = async () =&gt; &#123;  if (client?.isOpen) return;  try &#123;    client = createClient(&#123;      url: &quot;redis://localhost:6379&quot;, // 默认连接地址      // password: &#x27;your_password&#x27;,  // 如果需要密码验证    &#125;);    // 监听连接错误事件    client.on(&quot;error&quot;, (err) =&gt; console.error(&quot;Redis connection error:&quot;, err));    await client.connect();    console.log(&quot;Redis client connected&quot;);  &#125; catch (err) &#123;    console.error(&quot;Failed to connect to Redis:&quot;, err);    throw err;  &#125;&#125;;/** * 存储数据（支持对象/数组自动序列化） * @param &#123;string&#125; key - 存储键名 * @param &#123;object|array|string|number|boolean&#125; value - 存储值 * @param &#123;number&#125; [ttl] - 过期时间（秒），不传则永久保存 */const setKey = async (key, value, ttl) =&gt; &#123;  if (!client?.isOpen) await initializeRedis();  try &#123;    const serializedValue =      typeof value === &quot;string&quot; ? value : JSON.stringify(value);    if (ttl) &#123;      await client.setEx(key, ttl, serializedValue);    &#125; else &#123;      await client.set(key, serializedValue);    &#125;  &#125; catch (err) &#123;    console.error(`Failed to set key &quot;$&#123;key&#125;&quot;:`, err);    throw err;  &#125;&#125;;/** * 获取存储的数据（自动反序列化对象/数组） * @param &#123;string&#125; key - 要获取的键名 * @returns &#123;Promise&lt;any&gt;&#125; */const getKey = async (key) =&gt; &#123;  if (!client?.isOpen) await initializeRedis();  try &#123;    const value = await client.get(key);    if (!value) return null;    try &#123;      return JSON.parse(value);    &#125; catch &#123;      return value;    &#125;  &#125; catch (err) &#123;    console.error(`Failed to get key &quot;$&#123;key&#125;&quot;:`, err);    throw err;  &#125;&#125;;/** * 删除指定键 * @param &#123;string&#125; key - 要删除的键名 * @returns &#123;Promise&lt;boolean&gt;&#125; */const deleteKey = async (key) =&gt; &#123;  if (!client?.isOpen) await initializeRedis();  const result = await client.del(key);  return result &gt; 0;&#125;;const scanKeys = async (pattern, count = 100) =&gt; &#123;  if (!client?.isOpen) await initializeRedis();  const foundKeys = [];  let cursor = 0;  do &#123;    const reply = await client.scan(cursor, &#123; MATCH: pattern, COUNT: count &#125;);    cursor = reply.cursor;    foundKeys.push(...reply.keys);  &#125; while (cursor !== 0);  return foundKeys;&#125;;/** * 关闭 Redis 连接 */const closeRedis = async () =&gt; &#123;  if (client?.isOpen) &#123;    await client.quit();    console.log(&quot;Redis connection closed&quot;);  &#125;&#125;;module.exports = &#123;  initializeRedis,  setKey,  getKey,  deleteKey,  closeRedis,&#125;;

