// send.jsvar amqplib = require(&quot;amqplib&quot;);const url = `amqp://localhost`;const QUEUE_NAME = &quot;task1&quot;;async function sendMessage(message) &#123;  let connection;  try &#123;    connection = await amqplib.connect(url, &#123;      username: &quot;node&quot;,      password: &quot;123456&quot;,      port: &quot;5672&quot;,      vhost: &quot;/&quot;,    &#125;);    console.log(&quot;✅ 成功连接到 RabbitMQ 服务器&quot;);    // 2. 创建通道    const channel = await connection.createChannel();    console.log(&quot;🔄 通道已创建&quot;);    // 3. 声明队列（如果不存在则创建）    await channel.assertQueue(QUEUE_NAME, &#123;      durable: true, // 持久化队列（服务器重启后保留）    &#125;);    // 4. 发送消息    const sent = channel.sendToQueue(      QUEUE_NAME,      Buffer.from(message),      &#123; persistent: true &#125; // 消息持久化    );    if (sent) &#123;      console.log(`📤 消息已发送: $&#123;message&#125;`);    &#125; else &#123;      console.error(&quot;❌ 消息发送失败&quot;);    &#125;  &#125; catch (error) &#123;    console.error(&quot;🔥 发生错误:&quot;, error);  &#125; finally &#123;    // 5. 延迟500ms后关闭连接    if (connection) &#123;      await new Promise((resolve) =&gt; setTimeout(resolve, 500));      await connection.close();      console.log(&quot;🚪 连接已关闭&quot;);    &#125;    process.exit(0);  &#125;&#125;// 执行发送const message = process.argv[2] || &quot;你好，RabbitMQ！&quot;;sendMessage(message);

// receiver.jsvar amqplib = require(&quot;amqplib&quot;);const QUEUE_NAME = &quot;task1&quot;;// 构造连接URL（处理密码特殊字符）const url = `amqp://localhost`;async function startConsumer() &#123;  let connection;  try &#123;    // 1. 建立连接    connection = await amqplib.connect(url, &#123;      username: &quot;node&quot;,      password: &quot;123456&quot;,      port: &quot;5672&quot;,      vhost: &quot;/&quot;,    &#125;);    console.log(&quot;✅ 成功连接到 RabbitMQ 服务器&quot;);    // 2. 创建通道    const channel = await connection.createChannel();    console.log(&quot;🔄 通道已创建&quot;);    // 3. 声明队列（与发送端配置一致）    await channel.assertQueue(QUEUE_NAME, &#123;      durable: true, // 必须与发送端队列声明一致    &#125;);    console.log(`📭 正在监听队列：$&#123;QUEUE_NAME&#125;`);    // 4. 设置消费参数    channel.prefetch(1); // 每次只处理一个消息    console.log(&quot;⏳ 等待消息中... (按 CTRL+C 退出)&quot;);    // 5. 启动消费者    channel.consume(      QUEUE_NAME,      async (msg) =&gt; &#123;        if (msg) &#123;          try &#123;            const content = msg.content.toString();            console.log(`📥 收到消息: $&#123;content&#125;`);            // 模拟业务处理（例如耗时操作）            await new Promise((resolve) =&gt; setTimeout(resolve, 1000));            // 手动确认消息（确保 noAck: false）            channel.ack(msg);            console.log(&quot;✔️ 消息已确认&quot;);          &#125; catch (error) &#123;            console.error(&quot;⚠️ 消息处理失败:&quot;, error);            channel.nack(msg); // 否定确认并重新入队          &#125;        &#125;      &#125;,      &#123;        noAck: false, // 关闭自动确认      &#125;    );    // 保持进程运行    await new Promise(() =&gt; &#123;&#125;);  &#125; catch (error) &#123;    console.error(&quot;🔥 发生错误:&quot;, error);    process.exit(1);  &#125; finally &#123;    if (connection) &#123;      await connection.close();      console.log(&quot;🚪 连接已关闭&quot;);    &#125;  &#125;&#125;// 启动消费者startConsumer();

