// request.js&quot;use strict&quot;;const crypto = require(&quot;crypto&quot;);const util = require(&quot;util&quot;);const url = require(&quot;url&quot;);const qs = require(&quot;querystring&quot;);const fs = require(&quot;fs&quot;);/** * 不参与加签过程的 header key */const HEADER_KEYS_TO_IGNORE = new Set([  &quot;authorization&quot;,  &quot;content-type&quot;,  &quot;content-length&quot;,  &quot;user-agent&quot;,  &quot;presigned-expires&quot;,  &quot;expect&quot;,]);// do request examplefunction request(signParams) &#123;  signParams.headers = &#123;    ...signParams.headers,    [&quot;X-Date&quot;]: getDateTimeNow(),  &#125;;  signParams.bodySha = getBodySha(signParams.body);  // 正规化 query object， 防止串化后出现 query 值为 undefined 情况  for (const [key, val] of Object.entries(signParams.query)) &#123;    if (val === undefined || val === null) &#123;      signParams.query[key] = &quot;&quot;;    &#125;  &#125;  const authorization = sign(signParams);  return fetch(    `https://iam.volcengineapi.com/?$&#123;qs.stringify(signParams.query)&#125;`,    &#123;      headers: &#123;        ...signParams.headers,        Authorization: authorization,      &#125;,      method: signParams.method,      body: signParams.body,    &#125;  );&#125;function sign(params) &#123;  const &#123;    headers = &#123;&#125;,    query = &#123;&#125;,    region = &quot;&quot;,    serviceName = &quot;&quot;,    method = &quot;&quot;,    pathName = &quot;/&quot;,    accessKeyId = &quot;&quot;,    secretAccessKey = &quot;&quot;,    needSignHeaderKeys = [],    bodySha,  &#125; = params;  const datetime = headers[&quot;X-Date&quot;];  const date = datetime.substring(0, 8); // YYYYMMDD  // 创建正规化请求  const [signedHeaders, canonicalHeaders] = getSignHeaders(    headers,    needSignHeaderKeys  );  const canonicalRequest = [    method.toUpperCase(),    pathName,    queryParamsToString(query) || &quot;&quot;,    `$&#123;canonicalHeaders&#125;\n`,    signedHeaders,    bodySha || hash(&quot;&quot;),  ].join(&quot;\n&quot;);  const credentialScope = [date, region, serviceName, &quot;request&quot;].join(&quot;/&quot;);  // 创建签名字符串  const stringToSign = [    &quot;HMAC-SHA256&quot;,    datetime,    credentialScope,    hash(canonicalRequest),  ].join(&quot;\n&quot;);  // 计算签名  const kDate = hmac(secretAccessKey, date);  const kRegion = hmac(kDate, region);  const kService = hmac(kRegion, serviceName);  const kSigning = hmac(kService, &quot;request&quot;);  const signature = hmac(kSigning, stringToSign).toString(&quot;hex&quot;);  return [    &quot;HMAC-SHA256&quot;,    `Credential=$&#123;accessKeyId&#125;/$&#123;credentialScope&#125;,`,    `SignedHeaders=$&#123;signedHeaders&#125;,`,    `Signature=$&#123;signature&#125;`,  ].join(&quot; &quot;);&#125;function hmac(secret, s) &#123;  return crypto.createHmac(&quot;sha256&quot;, secret).update(s, &quot;utf8&quot;).digest();&#125;function hash(s) &#123;  return crypto.createHash(&quot;sha256&quot;).update(s, &quot;utf8&quot;).digest(&quot;hex&quot;);&#125;function queryParamsToString(params) &#123;  return Object.keys(params)    .sort()    .map((key) =&gt; &#123;      const val = params[key];      if (typeof val === &quot;undefined&quot; || val === null) &#123;        return undefined;      &#125;      const escapedKey = uriEscape(key);      if (!escapedKey) &#123;        return undefined;      &#125;      if (Array.isArray(val)) &#123;        return `$&#123;escapedKey&#125;=$&#123;val          .map(uriEscape)          .sort()          .join(`&amp;$&#123;escapedKey&#125;=`)&#125;`;      &#125;      return `$&#123;escapedKey&#125;=$&#123;uriEscape(val)&#125;`;    &#125;)    .filter((v) =&gt; v)    .join(&quot;&amp;&quot;);&#125;function getSignHeaders(originHeaders, needSignHeaders) &#123;  function trimHeaderValue(header) &#123;    return header.toString?.().trim().replace(/\s+/g, &quot; &quot;) ?? &quot;&quot;;  &#125;  let h = Object.keys(originHeaders);  // 根据 needSignHeaders 过滤  if (Array.isArray(needSignHeaders)) &#123;    const needSignSet = new Set(      [...needSignHeaders, &quot;x-date&quot;, &quot;host&quot;].map((k) =&gt; k.toLowerCase())    );    h = h.filter((k) =&gt; needSignSet.has(k.toLowerCase()));  &#125;  // 根据 ignore headers 过滤  h = h.filter((k) =&gt; !HEADER_KEYS_TO_IGNORE.has(k.toLowerCase()));  const signedHeaderKeys = h    .slice()    .map((k) =&gt; k.toLowerCase())    .sort()    .join(&quot;;&quot;);  const canonicalHeaders = h    .sort((a, b) =&gt; (a.toLowerCase() &lt; b.toLowerCase() ? -1 : 1))    .map((k) =&gt; `$&#123;k.toLowerCase()&#125;:$&#123;trimHeaderValue(originHeaders[k])&#125;`)    .join(&quot;\n&quot;);  return [signedHeaderKeys, canonicalHeaders];&#125;function uriEscape(str) &#123;  try &#123;    return encodeURIComponent(str)      .replace(/[^A-Za-z0-9_.~\-%]+/g, escape)      .replace(        /[*]/g,        (ch) =&gt; `%$&#123;ch.charCodeAt(0).toString(16).toUpperCase()&#125;`      );  &#125; catch (e) &#123;    return &quot;&quot;;  &#125;&#125;function getDateTimeNow() &#123;  const now = new Date();  return now.toISOString().replace(/[:-]|\.\d&#123;3&#125;/g, &quot;&quot;);&#125;// 获取 body sha256function getBodySha(body) &#123;  const hash = crypto.createHash(&quot;sha256&quot;);  if (typeof body === &quot;string&quot;) &#123;    hash.update(body);  &#125; else if (body instanceof url.URLSearchParams) &#123;    hash.update(body.toString());  &#125; else if (util.isBuffer(body)) &#123;    hash.update(body);  &#125;  return hash.digest(&quot;hex&quot;);&#125;module.exports = request;

