ext);      break;    case EffectType.TAKE_EVERY:      handleTakeEvery(env, effect, next);      break;    default:      return;  &#125;&#125;export function call(fn, ...args) &#123;  return createEffect(EffectType.CALL, [fn, args]);&#125;function handleCall(effect, next) &#123;  const &#123;    payload: [fn, args],  &#125; = effect;  if (typeof fn === &quot;function&quot;) &#123;    const res = fn(...args);    if (res instanceof Promise) &#123;      res.then((res) =&gt; next(res));    &#125; else &#123;      next(res);    &#125;  &#125;&#125;// delay 就是用 call 实现的延迟方法export function delay(timer = 0) &#123;  return call(function () &#123;    return new Promise((resolve) =&gt; &#123;      setTimeout(resolve, timer);    &#125;);  &#125;);&#125;export function put(action) &#123;  return createEffect(EffectType.PUT, [action]);&#125;function handlePut(env, effect, next) &#123;  const &#123; dispatch &#125; = env.store;  const [action] = effect.payload;  const res = dispatch(action);  next(res);&#125;export function take(action) &#123;  return createEffect(EffectType.TAKE, [action]);&#125;// take 的阻塞行为意味着需要能监听dispatch被触发// 使用观察者模式监听take的订阅function handleTake(env, effect, next) &#123;  const &#123; channel &#125; = env;  const [action] = effect.payload;  channel.take(action.type, () =&gt; &#123;    next(action);  &#125;);&#125;export function fork(generatorFn) &#123;  return createEffect(EffectType.FORK, [generatorFn]);&#125;// 接受一个generatorfunction handleFork(env, effect, next) &#123;  const [generatorFn] = effect.payload;  const task = runSaga(env, generatorFn);  next(task);&#125;export function takeEvery(action, generatorFn) &#123;  return createEffect(EffectType.TAKE_EVERY, [action, generatorFn]);&#125;function handleTakeEvery(env, effect, next) &#123;  const [action, generatorFn] = effect.payload;  return fork(function* () &#123;    while (true) &#123;      yield take(action);      yield fork(generatorFn);    &#125;  &#125;);&#125;export function all(generators) &#123;  return createEffect(EffectType.TAKE_EVERY, [generators]);&#125;const EffectType = &#123;  ALL: &quot;ALL&quot;,  CALL: &quot;CALL&quot;,  PUT: &quot;PUT&quot;,  TAKE: &quot;TAKE&quot;,  FORK: &quot;FORK&quot;,  TAKE_EVERY: &quot;TAKE_EVERY&quot;,&#125;;export class Channel &#123;  listeners = &#123;&#125;;  take(prop, fn) &#123;    if (!this.listeners[prop]) this.listeners[prop] = [];    this.listeners[prop].push(fn);  &#125;  // 触发监听  put(prop, ...args) &#123;    if (!this.listeners[prop]) return;    const listeners = this.listeners[prop];    // 一定要先删除再运行，避免take中会再次添加新的监听    this.listeners[prop] = void 0;    listeners.forEach((fn) =&gt; &#123;      fn(...args);    &#125;);  &#125;&#125;class Task &#123;  constructor(next) &#123;    this.next = next;  &#125;  cancel() &#123;    this.next(null, null, true);  &#125;&#125;

