var cloneGraph = function (node) &#123;  const map = new Map();  const cloneNode = new Node(node.val);  map.set(node.val, cloneNode)  const dfs = (cloneNode, node, neighbors) =&gt; &#123;    for (let n of node.neighbors) &#123;      if (map.has(n.val)) &#123;        neighbors.push(map.get(n.val))      &#125; else &#123;        const cloneNode = new Node(n.val)        neighbors.push(cloneNode)        map.set(n.val, cloneNode)        dfs(cloneNode, n, cloneNode.neighbors)      &#125;    &#125;  &#125;  dfs(cloneNode, node, cloneNode.neighbors)  return cloneNode;&#125;;

