var countAndSay = function (n) &#123;    if (n === 1) return &#x27;1&#x27;;    var res = &#x27;1&#x27;,        i = 1;    for (; i &lt; n; i++) &#123;        var str = &#x27;&#x27;;        var count = 0;        var stack = [];        for (var j = 0; j &lt; res.length; j++) &#123;            if (stack[0] !== undefined &amp;&amp; stack[0] !== res[j]) &#123;                str += stack.length + stack[0];                stack = [];            &#125;            stack.push(res[j])        &#125;        if (stack.length) &#123;            str += stack.length + stack[0];        &#125;        res = str;    &#125;    return res;&#125;;

