     if (node === cur) return count;      cur = cur.next;      count += 1;    &#125;    return -1;  &#125;  // 移除某个元素  remove(node) &#123;    const index = this.insert(node);    return this.removeAt(index);  &#125;  // 获取头节点  getHead() &#123;    return this._head.next;  &#125;&#125;

